 register = ()=>{Axios({

  method: "POST",
data: {
  username: registerUsername,
  password: registerPassword,
  langue1: registerL1,
  lvl1: registerLVL1,
  langue2: registerL2,
  lvl2: registerLVL2,
},withCredentials:true,
url:"http://localhost:4000/register",
}).then((res)=> console.log(res));

};

}


