import React,{Component} from "react";
import { Link } from 'react-router-dom';
import Axios from 'axios';
import './Conn.css';


export default class Conn  extends Component {
  
handleSubmit = e => {
  e.preventDefault();

const data = {
  username :this.email,
  password:this.motdepasse,

};

Axios.post('http://localhost:4000/login',data).then(
  res => {
    localStorage.setItem('token',res.data.token);
    console.log(res);
  }
).catch(
  err=> {
    console.log(err);
  }
)

  console.log(data);
};

  render() {
    return(
       
      <div>
          
          <div class="Title">
              <h2>Connecte toi à LingoPeer!</h2>
          </div>
          <div class="Connect">
              <fieldset>
              <div class="card ">                          

              <div class="card-header">
                  <legend>Se Connecter</legend>
                  </div>
                      <form onSubmit={this.handleSubmit} >
                          <div class="FirstInputs">
                          <div className ="form-group">
                          <div class="selects">
                          <div className ="form-group">

                          <label for="E-mail">Email :</label>
                           <input  type="email" placeholder="batou@gmail.com"  className ="form-control" onChange={e => this.email =e.target.value} ></input><br></br> 
                          </div>
                          <div className ="form-group">
                          <label for="Motdepasse">Mot de passe :</label>
                  <input   type="password" placeholder="Mot de passe"  className ="form-control" onChange={e => this.motdepasse =e.target.value}></input><br></br> 
                          </div>
                          </div>
                          <button type="conn" name="conn"  > Se Connecter</button>
                        
                          </div>
                          </div>
                      </form>
                      </div>
              </fieldset>
              <fieldset>
              <div class="card ">  
              <div class="card-header">
                  <legend>Continuer sans se Connecter</legend>
                  </div>
                  <div class="SecondInputs">
                      <form>
                          <label for="Nom">Nom :</label>
                          <input placeholder='Nom'/><br/>
                      </form>
                      <form>
                      <div class="selects">
                          <select class="Langue" name="Langue1">
                              <option selected disabled>Langue 1</option>
                              <option value="1">français</option>
                              <option value="2">Anglais</option>
                              <option value="3">Allemand</option>
                              <option value="4">Espagnol</option>
                              <option value="5">Italien</option>
                          </select>
                          <select class="Langue" name="Langue2">
                              <option selected disabled>Langue 2</option>
                              <option value="1">français</option>
                              <option value="2">Anglais</option>
                              <option value="3">Allemand</option>
                              <option value="4">Espagnol</option>
                              <option value="5">Italien</option>
                          </select>
                          </div>
                          <Link to="/apresConn">
                              <button type="conn" name="conn"> Se Connecter</button>
                          </Link>
                          </form>
                          </div>
                          </div>
              </fieldset>
              </div>
        </div>
  
      );
  }


}
