import Axios from 'axios';
import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import './Header.css';


export default class Header2 extends Component {

state = {};
componentDidMount(){

const config = {
headers :{
  Autho:'Bearer' +localStorage.getItem('token')
}
};

Axios.get('http://localhost:4000/user',config).then(
  res => {
this.setState({
  username:res.data
});
 },
  err => {
    console.log(err);
  }
)

}

render() {
  if (this.state.user) {
    return (
    <div class="Header">
    <h1>Lingo<span>Peer</span></h1>
    <nav>
        <ul>
          <Link to="/apresConn">
          <li>Home</li>
          </Link>
          <Link to="/Conn">
          <li>AboutUs</li>
          </Link>
          <Link to="/Conn">
          <li>Contact Us</li>
          </Link>
        </ul>
    </nav>
    <Link to="/Profile">
    <button id="utilisateurBTN" >{this.state.user.username}</button>
    </Link>
  
</div>)
  }


  return(
    <div class="Header">
          <h1>Lingo<span>Peer</span></h1>
          <nav>
              <ul>
                <Link to="/apresConn">
                <li>Home</li>
                </Link>
                <Link to="/Conn">
                <li>AboutUs</li>
                </Link>
                <Link to="/Conn">
                <li>Contact Us</li>
                </Link>
              </ul>
          </nav>
          <Link to="/Profile">
          <button id="utilisateurBTN" ></button>
          </Link>
        
    </div>

  )
}

}
