import React,{ useState } from 'react';
import { Link } from 'react-router-dom';
import './SignIn.css';
import Axios from 'axios';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';

export default class Signin extends React.Component {

state = {
  title:Signin
}

handleSubmit= e => {
e.preventDefault();

}


 /*States = () => {
  const [registerUsername,setRegisterUsername]=useState("");
  const [registerPassword,setRegisterPassword]=useState("");
  const [registerL1,setregisterL1]=useState("");
  const [registerLVL1,setregisterLVL1]=useState("");
  const [registerL2,setregisterL2]=useState("");
  const [registerLVL2,setregisterLVL2]=useState("");
  const [data, setData] = useState(null);
}
 

 register = ()=>{Axios({

  method: "POST",
data: {
  username: registerUsername,
  password: registerPassword,
  langue1: registerL1,
  lvl1: registerLVL1,
  langue2: registerL2,
  lvl2: registerLVL2,
},withCredentials:true,
url:"http://localhost:4000/register",
}).then((res)=> console.log(res));

};

 getUser = () => {
 
  Axios({
    method: "GET",
    withCredentials: true,
    url: "http://localhost:4000/user",
  }).then((res) => {
    setData(res.data);
    console.log(res.data);
  });
};

 click =() => {
  getUser();
  register();

}*/

render() {
  

  return    (<div>
    <div class="Title">
      <h2>Rejoins LingoPeer!</h2>
    </div>
    <div class="Connect">
      <fieldset>
          <legend>Données Personnelles</legend>
          <div class="FirstInputs">
          <form onSubmit={this.handleSubmit} >
            <div className ="form-group">
              <label for="Prenom">Prénom :</label>
            <input   type="text" placeholder="Prenom"  className ="form-control"/*onChange={e => setRegisterUsername(e.target.value)}*/></input><br></br> 
            </div>
            <div className ="form-group">
              <label for="Nom">Nom :</label>
            <input   type="text" placeholder="Nom"  className ="form-control"/*onChange={e => setRegisterUsername(e.target.value)}*/></input><br></br> 
            </div>
            <div className ="form-group">
              <label for="E-mail">Email :</label>
            <input   type="email" placeholder="batou@gmail.com"  className ="form-control"/*onChange={e => setRegisterUsername(e.target.value)}*/></input><br></br> 
            </div>

            <div className ="form-group">
              <label for="Motdepasse">Mot de passe :</label>
            <input   type="password" placeholder="Mot de passe"  className ="form-control"/*onChange={e => setRegisterUsername(e.target.value)}*/></input><br></br> 
            </div>

            <div className ="form-group">
              <label for="Motdepasse">Confirmation :</label>
            <input   type="Confirmation" placeholder="Confirmation"  className ="form-control"/*onChange={e => setRegisterUsername(e.target.value)}*/></input><br></br> 
            </div>

            <div class="form-row">
            <div class="form-group col-md-4">
            <label for="inputState">Langue maitrisée :</label>
              <select id="inputState" class="form-control">

              <option value="Français">Français</option>
              <option value="Anglais">Anglais</option>
              <option value="Allemand">Allemand</option>
              <option value="Espagnol">Espagnol</option>
              <option value="Italien">Italien</option>
      </select>
    </div>
    <div class="form-group col-md-4">
      <label for="inputState">Niveau</label>
      <select id="inputState" class="form-control">
        <option>Débutant</option>
         <option>Intermédiaire</option>
        <option>Expert</option>

      </select>
    </div>
    </div>
              <div className="form-group">
               <label for="exampleFormControlSelect1">Example select</label>
              <select class="form-control" id="exampleFormControlSelect1">
              <option>1</option>
              <option>2</option>
              <option>3</option>
              <option>4</option>
              <option>5</option>
               </select>
              </div>

          </form>   
