import React, { useEffect, useState, useRef } from 'react';
import './Room.css';
import io from "socket.io-client";
import Peer from "simple-peer";
import styled from "styled-components";
import Chat from "./Chat/Chat";
import authService from '../services/auth.service';

const Container = styled.div`
  height: 100vh;
  width: 100%;
  display: flex;
  flex-direction: column;
`;

const Row = styled.div`
  display: flex;
  width: 100%;
`;

const Video = styled.video`
  border: 1px solid blue;
  width: 50%;
  height: 50%;
`;

function Room() {
  const [yourID, setYourID] = useState("");
  const [users2, setUsers] = useState({});
  const [stream, setStream] = useState();
  const [receivingCall, setReceivingCall] = useState(false);
  const [caller, setCaller] = useState("");
  const [callerSignal, setCallerSignal] = useState();
  const [callAccepted, setCallAccepted] = useState(false);

  const userVideo = useRef();
  const partnerVideo = useRef();
  const socket = useRef();

  let myID;

  useEffect(() => {
    socket.current = io.connect("https://lingopeerbe.herokuapp.com/");

    const user = authService.getCurrentUser();
    const room = user.room;

    navigator.mediaDevices.getUserMedia({ video: true, audio: true }).then(stream => {
      setStream(stream);
      if (userVideo.current) {
        userVideo.current.srcObject = stream;
      }
    })

    socket.current.on('user', (user) => {
      console.log(user);
    })

    socket.current.on('yourID', (id) => {
      setYourID(id);
      myID = id;
      console.log(myID);
    })

    socket.current.on('allUsers', (users2) => {
      setUsers(users2);
      console.log(users2);
    })

    socket.current.on('hey', (data) => {
      setReceivingCall(true);
      setCaller(data.from);
      setCallerSignal(data.signal);
    })
  }, []);

  function callPeer(id) {
    const peer = new Peer({
      initiator: true,
      trickle: false,
      stream: stream,
    });

    peer.on("signal", data => {
      socket.current.emit("callUser", { userToCall: id, signalData: data, from: yourID })
    })

    peer.on("stream", stream => {
      if (partnerVideo.current) {
        partnerVideo.current.srcObject = stream;
      }
    });

    socket.current.on("callAccepted", signal => {
      setCallAccepted(true);
      peer.signal(signal);
    })

  }

  function acceptCall() {
    setCallAccepted(true);
    const peer = new Peer({
      initiator: false,
      trickle: false,
      stream: stream,
    });
    peer.on("signal", data => {
      socket.current.emit("acceptCall", { signal: data, to: caller })
    })

    peer.on("stream", stream => {
      partnerVideo.current.srcObject = stream;
    });

    peer.signal(callerSignal);
  }

  let UserVideo;
  if (stream){
    UserVideo = (
      <Video playsInline muted hidden ref={userVideo} autoPlay />
    );
  }

  let PartnerVideo;
  if (callAccepted) {
    UserVideo = (
      <Video playsInline muted ref={userVideo} autoPlay />
    );
    PartnerVideo = (
      <Video playsInline ref={partnerVideo} autoPlay />
    );
  }

  let incomingCall;
  if (receivingCall) {
    incomingCall = (
      <div>
        <h1>The other user is calling you</h1>
        <button onClick={acceptCall}>Accept</button>
      </div>
    )
  }
  return (
    <Container>
    <h1>Conversation</h1>
      <Row>
        {UserVideo}
        {PartnerVideo}
      </Row>
      <Chat/>
      <Row>
        {Object.keys(users2).map(key => {
          if (key === myID) {
            return null;
          }
          return (
            <button onClick={() => callPeer(key)}>Call the other user</button>
          );
        })}
      </Row>
      <Row>
        {incomingCall}
      </Row>
    </Container>
  );
}

export default Room;
