render() {
  

  return    (
    
      
          <form onSubmit={this.handleSubmit} >
          <h2>Rejoins LingoPeer!</h2>

          <legend>Données Personnelles</legend>
            <div className ="form-group">
              <label for="Prenom">Prénom :</label>
            <input   type="text" placeholder="Prenom"  className ="form-control"  onChange={e => this.prenom =e.target.value}></input><br></br> 
            </div>
            <div className ="form-group">
              <label for="Nom">Nom :</label>
            <input   type="text" placeholder="Nom"  className ="form-control"/*onChange={e => setRegisterUsername(e.target.value)}*/></input><br></br> 
            </div>
            <div className ="form-group">
              <label for="E-mail">Email :</label>
            <input   type="email" placeholder="batou@gmail.com"  className ="form-control"/*onChange={e => setRegisterUsername(e.target.value)}*/></input><br></br> 
            </div>

            <div className ="form-group">
              <label for="Motdepasse">Mot de passe :</label>
            <input   type="password" placeholder="Mot de passe"  className ="form-control"/*onChange={e => setRegisterUsername(e.target.value)}*/></input><br></br> 
            </div>

            <div className ="form-group">
              <label for="Motdepasse">Confirmation :</label>
            <input   type="Confirmation" placeholder="Confirmation"  className ="form-control"/*onChange={e => setRegisterUsername(e.target.value)}*/></input><br></br> 
            </div>
            
           
        
          
          <div className ="form-group" >
            <select class="Langue" name="Langue1" /**onChange={e => setregisterL1(e.target.value)}/ */ >
              <option selected disabled>Langue 1</option>
              <option value="Français">Français</option>
              <option value="Anglais">Anglais</option>
              <option value="Allemand">Allemand</option>
              <option value="Espagnol">Espagnol</option>
              <option value="Italien">Italien</option>
            </select>
           
            <select class="Niveau" name="Niveau1" /*onChange={e => setregisterLVL1(e.target.value)}*/ >
              <option selected disabled>Niveau 1</option>
              <option value="Débutant">Débutant</option>
              <option value="Intermédiaire">Intermédiaire</option>
              <option value="Expert">Expert</option>
              
            </select>
            
            <select class="Langue" name="Langue2" /*onChange={e => setregisterL2(e.target.value)}*/>
              <option selected disabled>Langue 2</option>
              <option value="Français">Français</option>
              <option value="Anglais">Anglais</option>
              <option value="Allemand">Allemand</option>
              <option value="Espagnol">Espagnol</option>
              <option value="Italien">Italien</option>
            </select>

            <select class="Niveau" name="Niveau2" /*onChange={e => setregisterLVL2(e.target.value)}*/ >
              <option selected disabled>Niveau 2</option>
              <option value="Débutant">Débutant</option>
              <option value="Intermédiaire">Intermédiaire</option>
              <option value="Expert">Expert</option>
              
            </select>

          </div>
          <Link to="/apresConn">
              <button className="btn btn-primary btn-block"  type="conn" name="conn" /*onClick={click}*/>Se Connecter</button>
          </Link>
        
          </form>   
  
 )
  
}
}
